import Layout from '../components/Layout';
import fetch from 'isomorphic-unfetch'

const Post = (props) => (
    <Layout>
        <h1>{props.props.countryName.name}</h1>
        <h2>{props.props.countryName.capital}</h2>
        <h3>{props.props.countryName.region}</h3>
        <h3>{props.props.countryName.subregion}</h3>
        <h3>{props.props.countryName.population}</h3>
        <img src={props.props.countryName.flag}/>
    </Layout>
)

Post.getInitialProps = async function (context) {
    const { name } = context.query
    const res = await fetch(`https://restcountries.eu/rest/v2/name/${name}`)
    const countryName = await res.json()

    console.log(`Fetched country name: ${countryName}`)
    //console.log(res)

    return {props: countryName}
}

export default Post;
