import Layout from '../components/Layout'
import fetch from 'isomorphic-unfetch'

const Post = (props) => (
    <Layout>
        {props.countries.map((country) => (
            <div key={country.name}>
                <img src={country.flag}/>
                <h1>{country.name}</h1>
                <h4>Region: {country.region}</h4>
                <h4>Sub-region: {country.subregion}</h4>
                <h4>Population: {country.population}</h4>
                <h4>Currency:{[country.currencies]}{country.currencies.name}</h4>
                <h4>Language: {country.languages.name}</h4>
            </div>


        ))}

        {/*<h1>{props.country.name}</h1>
        <h2>{props.country.capital}</h2>
        <h3>{props.country.region}</h3>
        <h3>{props.country.subregion}</h3>
        <h3>{props.country.population}</h3>
<img src={props.country.flag}/>*/}
    </Layout>
)

Post.getInitialProps = async function (context) {
    const {name}  = context.query
    const res = await fetch(`https://restcountries.eu/rest/v2/name/${name}`)
    const country = await res.json()

    console.log(`Fetched country name: ${country.name}`)
    console.log(JSON.stringify(country, null, 2))
    console.log(name)

    return { countries: country }
}

export default Post;
