import Layout from '../components/Layout';
import Link from 'next/link';

import fetch from 'isomorphic-unfetch';

const containerStyle = {
    marginTop: '10px',
    padding: '5px',
    border: '1px solid red',
    height: '70px'
}

const imageStyle = {
    width: '100px',
    height: '65px',
    marginLeft: '2%',
    float: 'left'
}

const container2Style = {
    border: '1px solid yellow',
    float: 'left',
    paddingTop: '2%'
}

const linkStyle = {
    textDecoration: 'none',
    marginLeft: '10px',
    border: '1px solid black',
    float: 'left',
    marginTop: '20px',
    fontSize: '20px',
    color: 'white',
    textTransform: 'uppercase'
}



const Index = (props) => (
    <Layout>
            {props.countries.map((country) => (
                <div key={country.name} style={containerStyle}>
                    <div>
                        <img style={imageStyle} src={country.flag}/>
                        <Link as={`/p/${country.name}`} href={`/post?name=${country.name}`}> 
                            <a style={linkStyle}>{country.name}</a>
                        </Link> 
                    </div>
                </div>
            ))}

    </Layout>
)

Index.getInitialProps = async function() {
    const res = await fetch('https://restcountries.eu/rest/v2/regionalbloc/au')
    const data = await res.json()

    return {
        countries: data
    }
}

export default Index;
