import Layout from '../components/Layout'
import fetch from 'isomorphic-unfetch'

const container = {
    margin: 'auto',
    width: '35%',
    border: '1px solid black',
    textAlign: 'center',
    marginTop: '20px',
    backgroundColor: 'white'
}

const imageStyle = {
    width: '100%'
}

const countryName = {
    fontSize: '30px',
    textTransform: 'uppercase',
    color: 'rgb(118, 165, 165)',
    letterSpacing: '2px',
    marginTop: '0px',
    marginBottom: '0px',
    padding: '15px'
}

const container2 = {
    border: '1px solid black',
    textAlign: 'left',
    margin: 'auto',
    width: '65%',
}

const Post = (props) => (
    <Layout>
        {props.countries.map((country) => (
            <div key={country.name} style={container}>
                <img style={imageStyle} src={country.flag}/>
                <h1 style={countryName}>{country.name}</h1>
                <div style={container2}>
                    <h4>Region: {country.region}</h4>
                    <h4>Sub-region: {country.subregion}</h4>
                    <h4>Population: {country.population}</h4>
                    <h4>Language: {country.languages[0].name}</h4>
                    <h4>Currency: {country.currencies[0].name} ({country.currencies[0].symbol})</h4> 
                </div>

            </div>   
        ))}
        <style jsx>{`
            h4 {
                color: rgb(95, 131, 131);
                text-transform: uppercase;
                font-size: 18px;
                font-weight: 400;
            }
        `}</style>
    </Layout>
)

Post.getInitialProps = async function (context) {
    const {name}  = context.query
    const res = await fetch(`https://restcountries.eu/rest/v2/name/${name}`)
    const country = await res.json()

    console.log(`Fetched country name: ${country.name}`)
    console.log(JSON.stringify(country, null, 2))
    console.log(name)

    return { countries: country }
}

export default Post;
