import Layout from '../components/Layout';
import Link from 'next/link';

import fetch from 'isomorphic-unfetch';

const containerStyle = {
    marginTop: '10px',
    padding: '5px',
    height: '64px',
    cursor: 'pointer'
}

const imageStyle = {
    width: '100px',
    height: '65px',
    marginLeft: '1%',
    float: 'left'
}

const linkStyle = {
    textDecoration: 'none',
    marginLeft: '10px',
    float: 'left',
    marginTop: '20px',
    paddingTop: '10px',
    fontSize: '20px',
    color: 'white',
    textTransform: 'uppercase',
    border: '1px solid black',
    width: '90%',
    height: '50px'
}

const Index = (props) => (
    <Layout>
            {props.countries.map((country) => (
                <div key={country.name} style={containerStyle} id="container">
                    <div>
                        <img style={imageStyle} src={country.flag}/>
                        <Link as={`/p/${country.name}`} href={`/post?name=${country.name}`}> 
                            <a style={linkStyle}>{country.name}</a>
                        </Link> 
                    </div>
                </div>
            ))}
    <style jsx>{`
        #container:hover {
            background-color: lightgrey;

        }
    `}</style>
    </Layout>
)

Index.getInitialProps = async function() {
    const res = await fetch('https://restcountries.eu/rest/v2/regionalbloc/au')
    const data = await res.json()

    return {
        countries: data
    }
}

export default Index;
