import Layout from '../components/Layout';
import fetch from 'isomorphic-unfetch';

const Post = (props) => (
    <Layout>
        <ul>
            {props.countries.map((countryName, index) => (
                <li key={index}>
                    <h2>{countryName.name}</h2>
                </li>
            ))}
        </ul>
        {/*<h1>{props.countryName.name}</h1>
        <h2>{props.countryName.capital}</h2>
        <h3>{props.countryName.region}</h3>
        <h3>{props.countryName.subregion}</h3>
        <h3>{props.countryName.population}</h3>
<img src={props.countryName.flag}/>*/}
    </Layout>
)

Post.getInitialProps = async function (context) {
    const { name } = context.query
    const res = await fetch(`https://restcountries.eu/rest/v2/name/${name}`)
    const data = await res.json()

    console.log(`Fetched country name: ${data.name}`)
    //console.log(res)

    return { countires: data }
}

export default Post;
