import Layout from '../components/Layout';
import Link from 'next/link';

import fetch from 'isomorphic-unfetch';

const containerStyle = {
    padding: '5px',
    border: '1px solid red',
    height: '80px'
}

const imageStyle = {
    width: '100px',
    marginLeft: '2%',
    float: 'left'
}

const container2Style = {
    border: '1px solid yellow',
    height: '60px',
    float: 'left'
}

const linkStyle = {
    textDecoration: 'none',
    marginLeft: '10px',
    alignText: 'center',
    border: '1px solid black'
}



const Index = (props) => (
    <Layout>

            {props.countries.map((country) => (
                <div key={country.name} style={containerStyle}>
                    <div>
                        <img style={imageStyle} src={country.flag}/> 
                    </div>
                    <div style={container2Style}>
                        <Link as={`/p/${country.name}`} href={`/post?name=${country.name}`}> 
                            <a style={linkStyle}>{country.name}</a>
                        </Link>  
                    </div>
                </div>
            ))}

    </Layout>
)

Index.getInitialProps = async function() {
    const res = await fetch('https://restcountries.eu/rest/v2/regionalbloc/au')
    const data = await res.json()

    return {
        countries: data
    }
}

export default Index;
