import Layout from '../components/Layout';
import fetch from 'isomorphic-unfetch';

const Post = (props) => (
    <Layout>
        <h1>{props.region.name}</h1>
        <h2>{props.region.capital}</h2>
        <h3>{props.region.region}</h3>
        <h3>{props.region.subregion}</h3>
        <h3>{props.region.population}</h3>
        <img src={props.region.flag}/>
    </Layout>
)

Post.getInitialProps = async function (context) {
    const { region } = context.query
    const res = await fetch(`https://restcountries.eu/rest/v2/name/${region}`)
    const country = await res.json()

    console.log(`Fetched country name: ${country.region}`)
    console.log(country)

    return { country }
}

export default Post;
