import Header from './Header';

const layoutStyle = {
    backgroundColor: 'rgb(118, 165, 165)',
    fontFamily: 'Arial, Helvetica, sans-serif',
    height: '100%'
}

const Layout = (props) => (
    <div style={layoutStyle}>
        <Header />
        {props.children}
    </div>
)

export default Layout;
