import Layout from '../components/Layout';
import fetch from 'isomorphic-unfetch'

const Post = (props) => (
    <Layout>
        <h1>{props.name.name}</h1>
        <h2>{props.name.capital}</h2>
        <h3>{props.name.region}</h3>
        <h3>{props.name.subregion}</h3>
        <h3>{props.name.population}</h3>
        <img src={props.name.flag}/>
    </Layout>
)

Post.getInitialProps = async function (context) {
    const { region } = context.query
    const res = await fetch(`https://restcountries.eu/rest/v2/name/${region}`)
    const name = await res.json()

    console.log(`Fetched country name: ${name.name}`)

    return { name }
}

export default Post;
