import Layout from '../components/Layout';
import Link from 'next/link';

import fetch from 'isomorphic-unfetch';

const listStyle = {  
    textAlign: 'center',
    listStyleType: 'none',
    paddingBottom: '10px',
    marginBottom: '10px',
    display: 'flex'
}

const linkStyle = {
    textDecoration: 'none',
    marginLeft: '10px',
    marginBottom: '10px',
}

const imageStyle = {
    width: '5%',
    display: 'inline'
}

const Index = (props) => (
    <Layout>

            {props.countries.map((country) => (
                <div key={country.name} style={listStyle}>
                    <div>
                        <img style={imageStyle} src={country.flag}/> 
                    </div>
                    <div>
                        <Link as={`/p/${country.name}`} href={`/post?name=${country.name}`}> 
                            <a style={linkStyle}>{country.name}</a>
                        </Link>  
                    </div>
                </div>
            ))}

    </Layout>
)

Index.getInitialProps = async function() {
    const res = await fetch('https://restcountries.eu/rest/v2/regionalbloc/au')
    const data = await res.json()

    return {
        countries: data
    }
}

export default Index;
