import Layout from '../components/Layout';
import Link from 'next/link';

import fetch from 'isomorphic-unfetch';

const Index = (props) => (
    <Layout>
        <h1>African Countries</h1>
        <ul>
            {props.regions.map(({countryName, index}) => (
                <li key={index}>
                    <Link as={`/name/${countryName.name}`} href={`/post?name=${countryName.name}`}>
                        <a>{countryName.name}</a>
                    </Link>
                </li>
            ))}
        </ul>
    </Layout>
)

Index.getInitialProps = async function() {
    const res = await fetch('https://restcountries.eu/rest/v2/region/africa')
    const data = await res.json()

    return {
        regions: data
    }
}

export default Index;
