import Link from 'next/link'

const linkStyle = {
    textDecoration: 'none',
    width: '100%',
    fontSize: '25px',
    color: 'rgb(118, 165, 165)',
    padding: '15px',
    textTransform: 'uppercase',
    letterSpacing: '1px'
  }

  const navStyle = {
    borderBottom: '1px solid white',
    paddingBottom: '15px',
    paddingTop: '15px',
    backgroundColor: 'white',
  }

const Header = () => (
    <div style={navStyle}>
        <Link href="/">
            <a style={linkStyle}>African Countries</a>
        </Link>
    <style jsx>{`
        a:hover {
            background-color: rgb(118, 165, 165);
            color: white;

        }
    `}</style>    
    </div>
)

export default Header;
